SELECT M.ID, M.COMMON_NAME, M.ORGANISATION_NAME, O.NAME, A.DNAME, M.STATUS, M.FIRST_ACTIVATED, M.CERT_EXPIRY, M.ORGANISATION_NUMBER, A.NAME, A.AGGREGATOR_TYPE, C.ID, C.NAME 
FROM MERCHANT_CATEGORY C, ORIGINATOR O, CAGROUP CA, MERCHANT M  LEFT OUTER JOIN AGGREGATOR A ON M.FK_AGGREGATOR = A.ID 
WHERE M.FK_MERCHANT_CATEGORY =  C.ID AND M.FK_ORIGINATOR= O.ID AND O.FK_CAGROUP = CA.ID